sqlMap.select:
WITH ORG_TREE AS (
) SELECT FOG.ORG_CD ORGCD , FOG.ORG_NM ORGNM , FOG.ORG_NM FULLORGNM , COUNT(INR.ADV_DTL_ID) CNTPOINTOUT , SUM(NVL(INR.IS_CLEARED, 0)) CNTPOINTCLEAR FROM (
) FOG LEFT OUTER JOIN ( SELECT DTL.ADV_DTL_ID , OTR.ROOT ORG_CD , DECODE( (SELECT COUNT(FILE_SN) FROM LETTNFILEDETAIL WHERE ATCH_FILE_ID = DTL.AFT_FILE_ID), 0, 0, 1 ) IS_CLEARED FROM TB_EXT_ORG_ADV_DTL DTL JOIN TB_EXT_ORG_ADV ADV ON DTL.ADV_ID = ADV.ADV_ID JOIN ORG_TREE OTR ON DTL.ORG_CD = OTR.ORG_CD WHERE ADV.MST_LAW_ID IN ( SELECT MST_LAW_ID FROM TB_MST_LAW_TPL WHERE OPT_TIME_YEAR = #optTimeYear#
AND OPT_TIME_HALF = #optTimeHalf#
) ) INR ON FOG.ORG_CD = INR.ORG_CD GROUP BY FOG.ORG_CD , FOG.ORG_NM , FOG.ORG_RANK ORDER BY FOG.ORG_RANK
